import React, { useState } from 'react';
import {
  importRSAPrivateKeyHex,
  rsaDecrypt,
  importAESRaw,
  aesGcmDecrypt,
  arrayBufferToHex,
  hexToArrayBuffer,
  base64ToArrayBuffer
} from '../utils/crypto';

export default function DecryptForm() {
  const [privateKeyHex, setPrivateKeyHex] = useState('');
  const [fileId, setFileId] = useState('');
  const [status, setStatus] = useState('');
  const [downloadUrl, setDownloadUrl] = useState(null);

  // Fetch file from server: GET /file/:id which returns X-Metadata header (base64) and file blob
  async function handleFetchAndDecrypt(e) {
    e.preventDefault();
    setStatus('Fetching file from server...');
    setDownloadUrl(null);
    try {
      if (!privateKeyHex) { setStatus('Provide private key HEX (PKCS8 DER)'); return; }
      if (!fileId) { setStatus('Provide file id'); return; }

      const resp = await fetch(`http://localhost:4000/file/${fileId}`);
      if (!resp.ok) {
        const j = await resp.json().catch(() => ({}));
        throw new Error(j.error || 'Fetch failed');
      }

      // metadata comes in X-Metadata header (base64 encoded)
      const metaB64 = resp.headers.get('x-metadata');
      if (!metaB64) throw new Error('Missing metadata header');
      const metaJson = JSON.parse(atob(metaB64));
      const encryptedAESKeyHex = metaJson.encryptedAESKeyHex;
      const ivHex = metaJson.ivHex;

      // get blob (ciphertext)
      const blob = await resp.blob();
      const ciphertextArrayBuffer = await blob.arrayBuffer();

      setStatus('Importing private key...');
      const priv = await importRSAPrivateKeyHex(privateKeyHex.trim());

      setStatus('Decrypting AES key with RSA private key...');
      const encryptedAESBuffer = hexToArrayBuffer(encryptedAESKeyHex);
      const rawAes = await rsaDecrypt(priv, encryptedAESBuffer);

      setStatus('Importing AES key...');
      const aesKey = await importAESRaw(rawAes);

      // iv
      const ivBuf = hexToArrayBuffer(ivHex);
      const ivUint8 = new Uint8Array(ivBuf);

      setStatus('Decrypting file (AES-GCM)...');
      const decrypted = await aesGcmDecrypt(aesKey, ivUint8, ciphertextArrayBuffer);

      // decrypted is payload = original file bytes || digest
      // split last 32 bytes as SHA-256 digest
      const decU8 = new Uint8Array(decrypted);
      if (decU8.length < 32) throw new Error('Decrypted payload too small');
      const originalBytes = decU8.slice(0, decU8.length - 32);
      const digestBytes = decU8.slice(decU8.length - 32);

      // verify digest
      const computedDigest = new Uint8Array(await crypto.subtle.digest('SHA-256', originalBytes));
      let ok = true;
      for (let i = 0; i < 32; i++) if (computedDigest[i] !== digestBytes[i]) { ok = false; break; }

      if (!ok) {
        setStatus('WARNING: Hash verification failed — file integrity check failed!');
        return;
      }

      setStatus('Success: integrity verified. Preparing download...');
      const blobOut = new Blob([originalBytes], { type: 'application/octet-stream' });
      const url = URL.createObjectURL(blobOut);
      setDownloadUrl({ url, filename: metaJson.originalFilename || 'decrypted.file' });
      setStatus('Decryption done — click download link.');
    } catch (err) {
      console.error(err);
      setStatus('Error: ' + err.message);
    }
  }

  return (
    <div style={{ border: '1px solid #ddd', padding: 16, borderRadius: 8 }}>
      <h2>Download & Decrypt</h2>
      <p>Provide file id (from upload response) and your RSA private key (HEX of PKCS8 DER).</p>
      <div>
        <label>File ID: </label>
        <input value={fileId} onChange={e => setFileId(e.target.value)} style={{ width: 300 }} />
      </div>
      <div style={{ marginTop: 8 }}>
        <textarea
          rows={6}
          value={privateKeyHex}
          onChange={e => setPrivateKeyHex(e.target.value.trim())}
          placeholder="Paste private key HEX (PKCS8 DER) here"
          style={{ width: '100%', fontFamily: 'monospace' }}
        />
      </div>
      <div style={{ marginTop: 8 }}>
        <button onClick={handleFetchAndDecrypt}>Fetch & Decrypt</button>
      </div>

      <div style={{ marginTop: 12 }}>
        <strong>Status:</strong> {status}
      </div>

      {downloadUrl && (
        <div style={{ marginTop: 12 }}>
          <a href={downloadUrl.url} download={downloadUrl.filename}>Download decrypted file ({downloadUrl.filename})</a>
        </div>
      )}
    </div>
  );
}
